INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'심리학을 만나게 된 것은 제 인생의 새로운 전환점과 시작이었습니다.  병아리 심리학도가 듬성듬성 나이 먹어 이젠 옛이야기를 꺼내는 누군가가 되었습니다.   상담심리학 영역과 임상심리학 영역을 넘나들면서 여러 가지 이론들을 채우다 보니 대학에서 기관에서 병원에서 일을 하는 제가 있었습니다.  지금부터는 이 곳에서 여러분들을 기다리고 있겠습니다.' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind101';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'거울속 저 사람은 누군가 했더니 어느새 눈꽃이 하얀 할멈 하나가 있었습니다.  오랜 세월 임상심리학을 가르치고 이젠 심리적인 어려움을 호소하는 내담자들을 만납니다.  오늘은 누가 올지 내일은 또 누구와 인생의 어려움을 풀어나갈지 알 수는 없지만 나를 찾아올 내담자들과 매일 새로운 곁을 나눕니다. ' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind102';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'제가 사는 이곳은 어디로든 조금만 나가면 푸른 제주바다가 넘실대고 있습니다.  어려움을 호소하는 사람들과 싸이코드라마 테라피를 통해서 텔레의 이끌림으로 만나는 일을 하고 있습니다.   멀리 있어도 늘 내 옆자리에 같이 할 수 있는 테라피를 손끝을 통해 전하고 싶습니다.' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind103';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'중독분야에 관심을 갖고 유학길에 올랐을 때 한국의 중독장애인들은 너무 갈 곳이 없구나.... 했습니다. 공부를 마치고 돌아온 이곳에서 또 받을 수 있는 서비스가 국한되어 사회에  적응하는 데 예전과  같은 어려움을 가지고 있구나.... 했습니다.  벗어날 수 없는 덫에 걸린 이들에게 여러분들의 망설임에 작은 도움들을 드리고자 합니다.' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind104';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'하늘 아래 가장 편한 동네로 이사를 오고  머무른  학교엔 장애 대학생들이 많아 그들과 보내는 시간이 길어졌습니다.  그들이 가진 신체적 핸디캡을 넘을 수는 없지만 마음까지 닫히는 아픔을 지켜보는 것은 늘 어떻게 하면이라는 고민을 하게합니다.  병원에서 대학에서 보냈던 시간들을 내담자를 위한 밑거름으로 활용하려고 합니다. ' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind105';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'상담이 좋아 상담을 하겠다 마음 먹고 이 일을 시작하며  일년이 십년 되고 또 지나면서 덥수룩 하던 청년은 간데없고 흰 머리칼만 수북한 아저씨가 되었습니다.    청소년과 지내다 부모님들과 만나고 지금은 대학 상담센터에서 일을 하고 있습니다.   늘 그날이 그날 같지만 여러분을 만나는 시간들은 새로운 소중함으로 다가옵니다. ' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind106';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'놀이치료사가 꿈이어서 오랫동안 아이들과 부대끼며 아이들이 어려워 하는 불편해 하는 실마리를 풀어주려 애썼습니다.  아이들의 문제가 곧 부모의 문제여서 어느 순간 부모를 만나기 위한 공부를 하는 제가 또 있었습니다.  늦깍이 엄마로 낯선 도시에서 아이와 씨름하면서 내담자들을 기다리고 있습니다.' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind107';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'하루 종일 병원에서 정신적인 문제가 있는 환자들을 만나고 검사를 하고 진단을 내리는 시간을 보내는 임상심리전문가 입니다.  문득 마음이 불편한 이들을 다르게 도울 수 있는 방법은 없을까 하며 병원이 아닌 다른 공간에서 기다리는 일을 하려고 합니다.   이상하다는 것과 평범하지 않다는 것이 무조건 문제가 되지는 않습니다.' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind108';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'안 녕하세요. 지혜의 부엉이라고 불리 우고 싶은 음악치료와 임상심리학을 전공한 해윰입니다.  어려서부터 항상 음악과 함께였던 제가 심리학으로 인해“사람”이라는 존재 그 자체를 느끼고 알게 되었습니다.  지금은“사람”을 통해 만남의 진정한 의미와 감사함을 체험하고 있는 중입니다. ' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind109';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'상담일을  시작하고 청소년들과 만나며 보낸 시간들이 어제 같은데 이젠 두 아이의 엄마로 시간이 훌쩍 갔습니다.  이젠 해가지는 창밖을 보다가도 문득, 세탁기가 돌아가는 소리를 듣다가도 문득 내담자가 나에게 어떤 의미인지 생각해보게 됩니다.  일 년 365일 하루 24시간 한 시간 60분.... 매 순간순간 내담자와 함께하는 이야기를 떠올립니다.' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind110';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'그 여름 날 푸른 소나기가 한차례 지나가고 모든 존재 하는 것.... 숨 쉬는 것들과 함께 할 수 있다는 것이 고마웠습니다. 그러나 제가 정말 고마운 것은 지금 이 자리에서 당신을 가다릴 수 있는 힘과 여유를 갖게 되었다는 것입니다. 누구나 조금씩 잠깐씩은 멈추기도 하고 돌아가기도 합니다. 이젠 멈춘 누군가에게 돌아가는 누군가에게 힘이 되어 드리고 싶습니다.   ' as self_introduction,3 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind111';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'광대처럼 살고 싶었던 적이 있었습니다. 내 안에 풀어놓지 못한 이야기들로 늘 마음이 힘들었던 때가 있었습니다.  상담을 공부하고 사람을 만나고 함께 웃고 울면서 다양한 테라피들을 배우게 했고 이런저런 경험들이 쌓였습니다. 인생을 살아가는 데에는 정답이 아닌 다양한 길이 존재함과 어떤 것이 최선인지는 알아가고 있습니다. 여러분의 다양한 인생길을 믿음으로 함께하려 합니다.' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind201';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'열등과 결핍의 아이가 상담학을 만나면서 자신에게 주어진 선물을 알아갔습니다. 아이들과, 학생들과, 부모들과 만나며 함께 풀어나간 깨알 선물보따리를 이제 더 많은 사람과 풀어보고 싶습니다. 마주하고 있지 않다고 어루만짐이 없는 것은 아닙니다. 보이지 않는 마음의 눈으로 어루만짐을 준비하겠습니다. ' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind202';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'조금 더 행복한 삶을 살기 위해 상담 공부를 시작하게 되었습니다. 그리고 이제는 내 삶의 행복뿐만 아니라, 청소년의 행복한 삶을 위해 고민하는 청소년상담자로 일을 하고 있습니다.눈앞에 있는 소소한 행복 거리를 놓치지 않도록, 그리고 앞으로의 삶이 행복한 삶이되기를 옆에서 지지하고 함께해주는 그런 상담자이길 바래 봅니다. ' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind203';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'그 누구도 아닌 `나 자신과 내 가족` 을 위해서 시작한 공부가 이제는 제 삶의 일부분이 되어가고 있습니다. 힘들고 지친 길을 함께 걸어가 주는 친구 같은 사람이 되고 싶습니다.제가 힘든 시기를 견뎌내 건강해진 것처럼 여러분들을 돕고 싶은 바램으로 망설임과 기다림을 가지고 있습니다.' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind204';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'저는... 마음의 소리, 영혼의 소리에 귀 기울이는 만남을 좋아합니다. 그래서 저는 늘 찾고 바라던 만남이 있습니다. 그런데 언젠가부터 그 만남을 찾은 것 같습니다. 바로 제가 그런 사람이 되는 것입니다. 우리는 완전한 상태가 아닌 조금씩 조금씩 되어가는 존재이며, 그 안에서 의미 있는 만남을 불러주는 이가 되어 기다리겠습니다. ' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind205';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'늘 상대방을 먼저 생각해주고 맞추며 살아가는 것이 행복이라고 착각했었던 때가 있었습니다. 그러면서 저의 끊임없는 배려가 채워지지 않는 공허로 다가오는 경험을 했습니다. 그러다 다양한 삶을 살고 있는 내담자들과 인생을 나누면서 아! 누군가를 위하기만 한다는 것이 전부는 아니구나! 깨닫게 되었습니다. 이젠 당신과 유유히 흐르는 강물처럼 나의 진정한 욕구를 채워가며 마음 가는대로 따라가 보려고 합니다.' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind206';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'사람이 좋아 봉사 동아리에 들어갔던 인연이 지금의 저를 있게 했습니다. 상담공부를 통해 아픔이 에너지가 되었고, 하나의 작품을 완성해 가는 과정이 되었습니다. 혼자서는 힘들지만 누군가 나를 기다려주고, 내 이야기를 아주 조금이라도 들어주는 시원한 바람 같은 존재가 있다면 우리는 지금을 더 사랑할 수 있을 것 같습니다. 당신의 작은 바람이 되고 싶습니다.' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind207';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'누구나 그럴지도 모르지만 저의 청소년기는 유난히 힘들어서 모든걸 포기하고 싶었었습니다. 어둠이 너무 길어서 다시 환하게 빛나게 되리라 생각하지 못했습니다. 어둡기만 했던 그 시절에 아무도 모르게 다가와 조용히 손 잡아준 멘토선생님때문에 지금의 제가 있습니다.  이제는 제가 누군가에게 그 단 한사람이 되어주고 싶어 상담을 공부하게 되었습니다.  지금 제가 만나는 한 사람... 한사람에게 그 한사람이 되길 소망하며 다가가고 있습니다.' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind208';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'비와 눈을 버금은 하늘도..... 천둥과 번개를 내뿜는 하늘도.... 모두 당신의 하늘입니다.누구든 맑은 하늘을 가질 수는 있지만 맑은 하늘만 계속 된다면 지루하고 재미없을 지도 모릅니다. 지금 당신에게 펼쳐진 하늘의 모양이 어떤지 무슨 색을 입고 있는지 초대해 주시겠습니까?  오늘 저는 당신의 하늘을 느끼고 싶습니다. 비온 뒤 굳는 땅처럼 당신의 힘듦을 같이 하고 싶습니다.' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind209';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'학교를 졸업하고 현장에서 10년 넘게 다양한 학생들을 만나면서 내가 그들에게 무엇을 해줄 수 있을까만 고민했습니다. 그제서야 저의 오만함을 깨달았습니다. 언제나 전 제 능력으로 세상을 바꾸고 상대방을 바로잡을 수 있을 거라 생각했는데... 그건 아무도 해 줄 수 없는 고독한 싸움이었습니다. 대신할 순 없지만 같이 할 수 있는 작업을 통해서 성장하고 돕고 싶습니다. 그리고 그것이 우리가 마지막까지 포기하지 않는 희망임을 나누고 싶습니다.' as self_introduction,1 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind210';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'사람들은 제게 말합니다. 뭘 그렇게까지 하냐고... 또 왜 그렇게 하냐고.... 그런데 이젠 그 의미를 알 것 같습니다. 그것 말고는 알지 못했고 그 방법만이 최선인 것 같았기 때문에 사람들의 이야기가 들리지 않았다는 것을... 이젠 여러분에게 충분히 다른 것을 선택할 수 있다는 것을 알려드리고 싶습니다. 그리고 그것이 내가 알고있던 것보다 충분히 괜찮다고 안전하다고 이야기 나누고 싶습니다.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind301';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'사람들은 힘들 때 누군가가 옆에서 내 이야기를 들어주고 나를 지지해 주었으면 합니다. 저 역시도 내 옆에 누군가가 있어서 내가 기댈 수 있다면 하고 바라곤 합니다. 이젠 제가 여러분을 기다리고 힘이 될 차례인 것 같습니다. 작고 사소한 것들이 얼마나 크고 버거운지 잊지 않고 기억하며 기다리겠습니다.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind302';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'눈을 뜨면 세상이 달라지고 고개를 돌리면 다른 것과 마주하게 되어서 때로는 새롭다는 것들이 두려움으로 다가오곤 합니다. 예전과 다른 너무 많은 새로움이 때론 우리에게 혼란과 어리석음으로 다가서서 또 다시 당황하게 되는 것 같습니다. 저의 실수들을 울타리로 안아 따듯함으로 바꾸고 싶습니다.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind303';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'누구나 어두운 밤을 보냅니다. 저 역시 그런 밤을 보내기도 했습니다. 그 밤, 우리는 깜깜한 어둠 속에서 어떤 것도 보이지 않고 내 옆엔 아무도 없다고 느낍니다. 돌이켜보면 제게 있었던 그런 밤에 간절하게 원했던 것은 누구라도 좋으니 내 이야기를 들어 줬음 좋겠다고 생각했었습니다. 이젠 제가 당신에게 어둠 속에서만 보이는 별처럼 어둠을 밝히는 작은 별이 되어 긴 밤을 함께 보내드리고 싶습니다.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind304';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'다람쥐 쳇바퀴 돌 듯 바쁘게 하루하루를 지내며 살다 보니 하늘을 바라볼 여유조차 사치인 것 같았던 때가 있었습니다. 그런 제게 가끔씩은 여유를 가지고 하늘을 바라봐도 좋다고 말 걸어 준 것이 상담공부였습니다. 이제는 늘 한 결 같이 이 자리에서 기다려 주는 사람이 되기 위해 앞으로 나아가고 있습니다.여기서 언제나 한 결 같이 기다리고 있겠습니다.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind305';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'가끔은 너무 빠르게만 가서 주변의 것들을 돌아보지 못하고 지나쳐버릴 때가 있습니다. 가끔은 너무 많은 생각과 물음들에 자기 자신을 가두어 둘 때도 있습니다. 그래서 가끔은 나는 없고 세상만 남겨져서 왜라는 질문들만 반복하곤 합니다. 질문의 늪에서 헤어 나오지 못하는 많은 당신들에게 인생에 작은 촛불이 되어 드리고 싶습니다. 멀리 있어도 불빛만 있다면 우린 그 길을 걸아나 갈 수 있을테니까요.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind306';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'우리 모두는 꿈을 꾸고 살아갑니다. 꿈을 꾼다는 것은 아무도 모르는 나만의 기쁨이고 행복입니다. 안타까운 것은 그 꿈이 때때로 우리를 지치고 힘들게도 한다는 것입니다. 그러나 다행인 것은 그 자잘한 어려움들이 우리를 또 날아오르게 하고 우리를 또 힘나게 도와준다는 것입니다. 지금 당신과 내가 나눈 이야기들이 당신을 새롭게 날아오를 수 있는 힘이 될 수 있도록 선물하고 싶습니다.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind307';
INSERT INTO `plymind_db`.`tbl_member_extra` (member_srl, social_type, social_id, login_count, serial_login_count, c_date, u_date, self_introduction, class_srl, domain_srl) SELECT member_srl, -1 as social_type, '' as social_id, 1 as login_count, 1 as serial_login_count, c_date, u_date,'다솜이라는 말뜻은 참 좋은 것 같습니다. 들어도 들어도 질리지 않고 자꾸만 듣고 싶은 말  ‘사랑’이기 때문입니다. 내가 정말 사랑하는 가족과 사람에게만 할 수 있는 말... 하지만 하기 쉬우면서 너무 어려운 말... 그 어려운 말을 어떻게 할 수 있는지 당신에게 다가가서 알려드리고 싶습니다. 사랑합니다. 다솜합니다. 당신과 함께 하는 저는 다솜한 사람입니다.' as self_introduction,2 as class_srl,1 as domain_srl FROM `plymind_db`.`tbl_member` WHERE user_id ='plymind308';
